import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';

@Component({
  selector: 'app-document-viewer',
  templateUrl: './document-viewer.component.html',
  styleUrls: ['./document-viewer.component.css']
})
export class DocumentViewerComponent implements OnChanges {
  @Input() documentContent: string = '';
  @Input() words =  this.documentContent.split(' ');

  ngOnChanges(changes: SimpleChanges) {
    if (changes['documentContent']) {
      this.documentContent = changes['documentContent'].currentValue;
    }
  }

