import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';

@Component({
  selector: 'app-document-viewer',
  templateUrl: './document-viewer.component.html',
  styleUrls: ['./document-viewer.component.css']
})
export class DocumentViewerComponent implements OnChanges {
  @Input() documentContent: string = '';
  // @Input() labelColorMap: Record<string, string> = {};

  annotatedDocumentContent: string = '';

  ngOnChanges(changes: SimpleChanges) {
    if (changes['documentContent']) {
      this.annotatedDocumentContent = changes['documentContent'].currentValue;
    }
  }

  onDocumentSelected(event: any) {
    console.log('event: ' + event.target.selectionStart);
    // const selection = event.target.innerHTML.substring(
    //   event.target.selectionStart,
    //   event.target.selectionEnd
    // );
    // console.log('selection: ' + selection);
    // const annotatedText = `<span style="background-color: ${
    //   this.labelColorMap[this.selectedLabel]
    // }">${selection}</span>`;
    // this.annotatedDocumentContent = this.annotatedDocumentContent.replace(
    //   selection,
    //   annotatedText
    // );
  }

  selectedLabel: string = '';

  onLabelSelected(label: string) {
    this.selectedLabel = label;
  }
}
