import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-label-list',
  templateUrl: './label-list.component.html',
  styleUrls: ['./label-list.component.css']
})
export class LabelListComponent {
  @Input() labelList: {label:string, color:string}[] = [];
  labelListInput = '';
  selectedLabel: string | null = null;

  addLabel() {
    if (this.labelListInput) {
      this.labelList.push({label: this.labelListInput, color: 'red'});
      this.labelListInput = '';
    }
  }

  onLabelSelect(label: {label:string, color:string}) {
    this.selectedLabel = label;
    console.log(`Selected label: ${this.selectedLabel}`);
  }
}
