import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-label-list',
  templateUrl: './label-list.component.html',
  styleUrls: ['./label-list.component.css']
})
export class LabelListComponent {
  @Input() labelList: string[] = [];
  labelListInput = '';
  selectedLabel: string | null = null;

  labelColorMap = {
    SKILLS: 'blue',
    EXPERIENCE: 'green',
    DIPLOMA: 'purple',
    DIPLOMA_MAJOR: 'red'
  };

  addLabel() {
    if (this.labelListInput && !this.labelList.includes(this.labelListInput)) {
      this.labelList.push(this.labelListInput);
      this.labelListInput = '';
    }
  }

  onLabelSelect(label: string) {
    this.selectedLabel = label;
    console.log(`Selected label: ${this.selectedLabel}`);
  }
}
