import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-document-viewer',
  templateUrl: './document-viewer.component.html',
  styleUrls: ['./document-viewer.component.css']
})
export class DocumentViewerComponent {
  @Input()
  documentContent!: string;
  selectedLabel!: boolean | null;
  annotations: any;

  // annotateDocument() {
  //   console.log('Document annotated');
  //   console.log(this.documentContent)
  // }
  onTextSelected(event: any) {
    const selection = window.getSelection();
    if (selection && !selection.isCollapsed && this.selectedLabel) {
      const range = selection.getRangeAt(0);
      const start = range.startOffset;
      const end = range.endOffset;
      const text = selection.toString();
      const annotation = { start, end, label: this.selectedLabel, text };
      this.annotations.push(annotation);
    }
  }

}
